mapper.select:
select a.id, a.title, a.author, b.desc as category, a.description from book a left join category b on a.category = b.code where 1=1
and a.title like concat('%', #{title}, '%')
order by a.createdtime desc limit #{startIndex},#{endIndex}

mapper.update:
update book
set title = #{title}, author = #{author}, category = #{category}, description = #{description}
where id = #{id}
